definition.script:
@NonCPS
def script(){
Jenkins.instance.items.collect{
    item->item.getAllJobs().findAll{
        job->!(job.name ==~ /SYSTEM_.*/) && !job.isInQueue() && !job.isBuilding()
    }
}.flatten().each{job->
    def parent = job.getParent().name
    println "Scheduling $parent/$job.name"
    job.scheduleBuild()
}
}

script()
