definition.script:
@NonCPS
def script(){
Jenkins.instance.nodes.each{slave->
    def nodename = slave.name
    echo "running node $nodename"
    node(nodename) {
        sh 'uptime'
        sh 'docker version'
        // sh 'docker images -q | xargs docker rmi || true'
        sh 'docker pull ubuntu:14.04'
    }
}
}

script()
