definition.script:
for (computer in Jenkins.instance.computers) {
    def countIdle = computer.countIdle()
    def countBusy = computer.countBusy()
  	def isOffline = computer.isOffline()
    println "Computer: $computer.name, Busy: $countBusy, Idle: $countIdle, isOffline: $isOffline"
    if(isOffline){
        println "Deleting $computer.name"
        computer.doDoDelete()
    }
}
